import React, { useState } from 'react'

const LogIn = () => {
    const [validatation, setvalidatation] = useState(false)
    const [ticketData, setticketData] = useState({
        email: '',
        password: '',
        confirmPassword: '',
    })
    const LogIn = (e) => {
        setvalidatation(true)
        e.preventDefault()
        console.log('ticketData',ticketData);
    }
    return (
        <div>
            <form action="">
                <input type="email" name='email' placeholder='Enter email'  onChange={(e) => setticketData({ ...ticketData, [e.target.name]: e.target.value })} />
                {
                    validatation && ticketData.email == "" && <small style={{ color: "red" }}>email is required</small>
                }
                <input type="password" name='password' placeholder='enter  password' onChange={(e) => setticketData({ ...ticketData, [e.target.name]: e.target.value })} />
                {
                    validatation && ticketData.password == "" && <small style={{ color: "red" }}>password is required</small>
                }
                <input type="password" name='confirmPassword' placeholder='confirm password' onChange={(e) => setticketData({ ...ticketData, [e.target.name]: e.target.value })} />
                {
                    validatation && ticketData.confirmPassword == "" && <small style={{ color: "red" }}>password is required</small>
                }
                <button onClick={(e) => LogIn(e)}>Sign Up</button>
            </form>
        </div>
    )
}

export default LogIn
